import React, { useState } from "react";
import * as sessionActions from "../../store/session";
import { useDispatch, useSelector } from "react-redux";
import { Link, Redirect } from "react-router-dom";
import './SignupFormPage.css'

function SignupFormPage() {
  const dispatch = useDispatch();
  const sessionUser = useSelector(state => state.session.user);
  const [display_name, setDisplayName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errors, setErrors] = useState([]);
  const [isSignedUp, setIsSignedUp] = useState(false); 

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrors([]);

    try {
      await dispatch(sessionActions.signup({ email, password, display_name }));
      setIsSignedUp(true); // set isSignedUp to true on successful signup
    } catch (res) {
      const data = await res.json();
      if (data && data.errors) {
        setErrors(data.errors);
      }
    }
  };

  if (sessionUser) {
    return <Redirect to="/" />;
  }

  if (isSignedUp) { // redirect to login page on successful signup
    return <Redirect to="/login" />;
  }


  return (
    <div className="signup-page">
          <div className="content">
              <div className="left-content">
                  <h1 id="left-title">Join the <span className="codeOverflow">codeOverflow</span> community</h1>
                  <ul className=" signup-content">
                      <div id="item"><svg className="signup-svg" width="26" height="26" ><path opacity=".5" d="M4.2 4H22a2 2 0 012 2v11.8a3 3 0 002-2.8V5a3 3 0 00-3-3H7a3 3 0 00-2.8 2z" fill = "#0a95ff"></path><path d="M1 7c0-1.1.9-2 2-2h18a2 2 0 012 2v12a2 2 0 01-2 2h-2v5l-5-5H3a2 2 0 01-2-2V7zm10.6 11.3c.7 0 1.2-.5 1.2-1.2s-.5-1.2-1.2-1.2c-.6 0-1.2.4-1.2 1.2 0 .7.5 1.1 1.2 1.2zm2.2-5.4l1-.9c.3-.4.4-.9.4-1.4 0-1-.3-1.7-1-2.2-.6-.5-1.4-.7-2.4-.7-.8 0-1.4.2-2 .5-.7.5-1 1.4-1 2.8h1.9v-.1c0-.4 0-.7.2-1 .2-.4.5-.6 1-.6s.8.1 1 .4a1.3 1.3 0 010 1.8l-.4.3-1.4 1.3c-.3.4-.4 1-.4 1.6 0 0 0 .2.2.2h1.5c.2 0 .2-.1.2-.2l.1-.7.5-.7.6-.4z" fill = "#0a95ff"></path></svg><li id="list-items">Get unstuck â€” ask a question</li></div>
                      <div id="item"><svg className="signup-svg" width="26" height="26" ><path d="M12 .7a2 2 0 013 0l8.5 9.6a1 1 0 01-.7 1.7H4.2a1 1 0 01-.7-1.7L12 .7z" fill = "#0a95ff"></path><path opacity=".5" d="M20.6 16H6.4l7.1 8 7-8zM15 25.3a2 2 0 01-3 0l-8.5-9.6a1 1 0 01.7-1.7h18.6a1 1 0 01.7 1.7L15 25.3z" fill="#0a95ff"></path></svg><li id="list-items">Unlock new privileges like voting and commenting</li></div>
                      <div id="item"><svg className="signup-svg" width="26" height="26" ><path d="M14.8 3a2 2 0 00-1.4.6l-10 10a2 2 0 000 2.8l8.2 8.2c.8.8 2 .8 2.8 0l10-10c.4-.4.6-.9.6-1.4V5a2 2 0 00-2-2h-8.2zm5.2 7a2 2 0 110-4 2 2 0 010 4z" fill = "#0a95ff"></path><path opacity=".5" d="M13 0a2 2 0 00-1.4.6l-10 10a2 2 0 000 2.8c.1-.2.3-.6.6-.8l10-10a2 2 0 011.4-.6h9.6a2 2 0 00-2-2H13z" fill = "#0a95ff"></path></svg><li id="list-items">Save your favorite tags, filters, and jobs</li></div>
                      <div id="item"><svg className="signup-svg" width="26" height="26" ><path d="M21 4V2H5v2H1v5c0 2 2 4 4 4v1c0 2.5 3 4 7 4v3H7s-1.2 2.3-1.2 3h14.4c0-.6-1.2-3-1.2-3h-5v-3c4 0 7-1.5 7-4v-1c2 0 4-2 4-4V4h-4zM5 11c-1 0-2-1-2-2V6h2v5zm11.5 2.7l-3.5-2-3.5 1.9L11 9.8 7.2 7.5h4.4L13 3.8l1.4 3.7h4L15.3 10l1.4 3.7h-.1zM23 9c0 1-1 2-2 2V6h2v3z" fill = "#0a95ff"></path></svg><li id="list-items">Earn reputation and badges</li></div>
                  </ul>
              </div>
              <div className="right-content">
                <form className="signup-form credentials" onSubmit={handleSubmit}>
                {errors.map(error => <li key={error}>{error}</li>)}
                    <label className="su-label"htmlFor="sun">Display name</label>
                      <input type="text" className="su-input" value={display_name} id="sun" placeholder="enter desired username"onChange={(e) => setDisplayName(e.target.value)}></input>

                    <label className="su-label" htmlFor="sul">Email</label>
                      <input type="text" className="su-input"value={email} id="sul" placeholder="email" onChange={(e) => setEmail(e.target.value)} required></input>

                    <label className="su-label" htmlFor="sup">Password</label>
                      <input type="password" value={password} className="su-input" id="sup" placeholder="password" onChange={(e) => setPassword(e.target.value)} required></input>

                    <button className="login-button sign-up-button">Sign up</button>
                </form>
                <div className="two-links">
                <div>Already have an account?
                  <Link to="/login">
                    <div>Log in</div>
                  </Link>
                </div>
        </div>
              </div>
          </div>
    </div>
  );
}

export default SignupFormPage;
