export function QueryParamProvider({
  children,
  adapter,
  options,
}: QueryParamProviderProps) {
  const Adapter = adapter;
  return Adapter ? (
    <Adapter>
      {(adapter) => (
        <QueryParamProviderInner adapter={adapter} options={options}>
          {children}
        </QueryParamProviderInner>
      )}
    </Adapter>
  ) : (
    <QueryParamProviderInner options={options}>
      {children}
    </QueryParamProviderInner>
  );
}

